package ru.lemaitre.mythirdrecycleview

import android.os.Bundle
import androidx.fragment.app.Fragment
import androidx.recyclerview.widget.GridLayoutManager
import kotlinx.android.synthetic.main.fragment_grid_image.*
import ru.lemaitre.mythirdrecycleview.adapters.ImageAdapter

class GridImageFragment:Fragment(R.layout.fragment_grid_image) {

    private var listImages = listOf<Images>(
        Images("https://www.pnp.ru/upload/entities/2019/12/18/article/detailPicture/90/ac/96/c7/28dc349f219c5ff70af0b64bbae3632b.jpg"),
        Images("data:image/jpeg;base64,"),
        Images("https://bipbap.ru/wp-content/uploads/2017/10/0_8eb56_842bba74_XL-640x400.jpg"),
        Images("https://s00.yaplakal.com/pics/pics_original/5/7/8/13224875.jpg"),
        Images("https://ulibky.ru/wp-content/uploads/2019/09/maynkraft_na_avu_1_17123234.jpg"),
        Images("https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcScGA0NCsv4Nxcx8eDaMW8ABIWj3NJS0YqE-g&usqp=CAU"),
        Images("https://lh3.googleusercontent.com/proxy/7L5SyXfuIquG5q8QxPHCn2lAvQwIQTLAMKHnzS6L98zv4aqktjfKYnyYjNyLNq0cpyFRSmAq4kjlybvMFO4Ky0xrWWsregjA0jH1QMRykYjIUIbTpSqXo9_k")
    )


    private var imagesAdapter by AutoClearedValue<ImageAdapter>(this)

    override fun onActivityCreated(savedInstanceState: Bundle?) {
        super.onActivityCreated(savedInstanceState)
        initList()
        imagesAdapter.updateImagesList(listImages + listImages + listImages + listImages)
        imagesAdapter.notifyDataSetChanged()
    }

    private fun initList() {
        imagesAdapter = ImageAdapter()
        with(gridImageRecyclerView) {
            adapter = imagesAdapter
            layoutManager = GridLayoutManager(requireContext(),2)
            addItemDecoration(ItemOffsetDecoration(requireContext()))
            setHasFixedSize(true)
        }
    }

}